INSERT INTO `ps_category_lang` VALUES (1,1,3,'Root','','root','','',''),(2,1,3,'ימחק','<p style=\"text-align: right;\">כאן יימכרו עגבניות סקוטיות</p>','imahek','','',''),(8,1,3,'ירקות','<p> </p>\r\n<h4 style=\"text-align: right;\" dir=\"RTL\"><span style=\"background-color: #ffffff; font-size: 12pt; color: #000000;\">אם אתם כבר אוכלים בחוץ, תאכלו במקום שמכבד את עצמו</span></h4>\r\n<p style=\"text-align: right;\" dir=\"RTL\"><span style=\"background-color: #ffffff; font-size: 12pt; color: #000000;\">השימוש בבשר מעובד אליו מוסיפים סוכר, סירופ תירס, שומן ומלח יוצרות \"מזון\" עתיר קלוריות וריק מערך תזונתי. השימוש בחומרים האלה הוא שגורם לנו <a href=\"http://www.mako.co.il/food-cooking_magazine/Article-6e78b042b7ad141006.htm\"><span style=\"background-color: #ffffff; color: #000000;\">להתמכר למזון</span></a>, ובגלל זה אנחנו חוזרים פעם אחר פעם לאותו המקום ולאותה המנה. מה אפשר לעשות כדי להפחית את הנזק? זה הזמן להתחיל להתייחס לאיכות המזון המונחת לכם בצלחת.</span></p>\r\n<p> </p>','erakot','','',''),(9,1,3,'ירקות שורש','<p style=\"text-align: right;\"><span style=\"color: #000000; font-size: 12pt;\">מגוון רחב של ירקות שורש</span></p>\r\n<p style=\"text-align: right;\"><span style=\"color: #000000; font-size: 12pt;\">הארוחה שלכם תראה אחרת לגמרי ולא תהיה מזיקה אם, למשל, היא תכלול ההמבורגר עשוי 100% בקר או נאגטס עשויים 100% חזה עוף, לחמנייה עשויה 100% קמח חיטה מלא או שיפון, שפע ירקות טריים ושומן בריא (כמו טחינה, לדוגמא)</span>.</p>','erakot-shoresh','','',''),(10,1,3,'עלים ירוקים','<p style=\"text-align: right;\"><span style=\"color: #000000; font-size: 12pt;\">ואם בחרתם לאכול בחוץ, פשוט נסו למצוא מקומות שמכבדים את עצמם ואת איכות חומרי הגלם בהם הם עושים שימוש. אל תאכלו את כל מה שמוכרים לכם: השינוי בדרישותיכם כצרכנים הוא זה שיוביל לשינוי ברמת הייצור. חישבו היטב מה עבר על המזון עד שהגיע לצלחת שלכם, והפנימו כי מגפת ההשמנה עולה בהתמדה במקומות בהם ניזונים בעיקר ממזון מהיר, מעובד וריק מערך תזונתי.מגוון עלים ירוקים</span></p>','alim','','',''),(11,1,3,'מוצרי פלסטיק','<p style=\"text-align: right;\">מגוון מוצרי פלסטיק איכותיים</p>','plastic','','',''),(12,1,3,'כלים חד פעמיים','<p style=\"text-align: right;\">מגוון כלים חד פעמיים</p>','hadpeami','','',''),(13,1,3,'מעמדים','<p style=\"text-align: right;\">מגוון מעמדים</p>','maamadim','','','');
